pragma solidity ^0.8.0;

/**
 * @title Curve 3Pool Strategy
 * @notice Investment strategy for investing stablecoins via Curve 3Pool
 * @author Stabl Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { ICurvePool } from "./ICurvePool.sol";
import { ICRVMinter } from "./ICRVMinter.sol";
import { IERC20, InitializableAbstractStrategy } from "../utils/InitializableAbstractStrategy.sol";
import { StableMath } from "../utils/StableMath.sol";
import { Helpers } from "../utils/Helpers.sol";

abstract contract BaseCurveStrategy is InitializableAbstractStrategy {
    using StableMath for uint256;
    using SafeERC20 for IERC20;

    uint256 internal constant maxSlippage = 1e16; // 1%, same as the Curve UI
    address internal pTokenAddress;

    /**
     * @dev Deposit asset into the Curve 3Pool
     * @param _asset Address of asset to deposit
     * @param _amount Amount of asset to deposit
     */
    function deposit(address _asset, uint256 _amount)
        external
        override
        virtual
        onlyVault
        nonReentrant
    {
        require(_amount > 0, "Must deposit something");
        emit Deposit(_asset, address(platformAddress), _amount);
        // 3Pool requires passing deposit amounts for all 3 assets, set to 0 for
        // all
        uint256[3] memory _amounts;
        uint256 poolCoinIndex = _getCoinIndex(_asset);
        // Set the amount on the asset we want to deposit
        _amounts[poolCoinIndex] = _amount;
        ICurvePool curvePool = ICurvePool(platformAddress);
        uint256 assetDecimals = Helpers.getDecimals(_asset);
        uint256 depositValue = _amount.scaleBy(18, assetDecimals).divPrecisely(
            curvePool.get_virtual_price()
        );
        uint256 minMintAmount = depositValue.mulTruncate(
            uint256(1e18) - maxSlippage
        );
        // Do the deposit to 3pool
        curvePool.add_liquidity(_amounts, minMintAmount);
        _lpDepositAll();
    }

    function _lpDepositAll() internal virtual;

    /**
     * @dev Deposit the entire balance of any supported asset into the Curve 3pool
     */
    function depositAll() external override virtual onlyVault nonReentrant {
        uint256[3] memory _amounts = [uint256(0), uint256(0), uint256(0)];
        uint256 depositValue = 0;
        ICurvePool curvePool = ICurvePool(platformAddress);
        uint256 curveVirtualPrice = curvePool.get_virtual_price();

        for (uint256 i = 0; i < assetsMapped.length; i++) {
            address assetAddress = assetsMapped[i];
            uint256 balance = IERC20(assetAddress).balanceOf(address(this));
            if (balance > 0) {
                uint256 poolCoinIndex = _getCoinIndex(assetAddress);
                // Set the amount on the asset we want to deposit
                _amounts[poolCoinIndex] = balance;
                uint256 assetDecimals = Helpers.getDecimals(assetAddress);
                // Get value of deposit in Curve LP token to later determine
                // the minMintAmount argument for add_liquidity
                depositValue =
                    depositValue +
                    balance.scaleBy(18, assetDecimals).divPrecisely(
                        curveVirtualPrice
                    );
                emit Deposit(assetAddress, address(platformAddress), balance);
            }
        }

        uint256 minMintAmount = depositValue.mulTruncate(
            uint256(1e18) - maxSlippage
        );
        // Do the deposit to 3pool
        curvePool.add_liquidity(_amounts, minMintAmount);
        // Deposit into Gauge, the PToken is the same (3Crv) for all mapped
        // assets, so just get the address from the first one
        _lpDepositAll();
    }

    function _lpWithdraw(uint256 numPTokens) internal virtual;

    /**
     * @dev Withdraw asset from Curve 3Pool
     * @param _recipient Address to receive withdrawn asset
     * @param _asset Address of asset to withdraw
     * @param _amount Amount of asset to withdraw
     */
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override virtual onlyVault nonReentrant {
        require(_amount > 0, "Invalid amount");

        emit Withdrawal(_asset, address(assetToPToken[_asset]), _amount);

        (uint256 contractPTokens, , uint256 totalPTokens) = _getTotalPTokens();

        uint256 coinIndex = _getCoinIndex(_asset);
        int128 curveCoinIndex = int128(uint128(coinIndex));
        // Calculate the max amount of the asset we'd get if we withdrew all the
        // platform tokens
        ICurvePool curvePool = ICurvePool(platformAddress);
        // Calculate how many platform tokens we need to withdraw the asset
        // amount in the worst case (i.e withdrawing all LP tokens)
        uint256 maxAmount = curvePool.calc_withdraw_one_coin(
            totalPTokens,
            curveCoinIndex
        );
        uint256 maxBurnedPTokens = (totalPTokens * _amount) / maxAmount;

        // Not enough in this contract or in the Gauge, can't proceed
        require(totalPTokens > maxBurnedPTokens, "Insufficient 3CRV balance");
        // We have enough LP tokens, make sure they are all on this contract
        if (contractPTokens < maxBurnedPTokens) {
            _lpWithdraw(maxBurnedPTokens - contractPTokens);
        }

        uint256[3] memory _amounts = [uint256(0), uint256(0), uint256(0)];
        _amounts[coinIndex] = _amount;
        curvePool.remove_liquidity_imbalance(_amounts, maxBurnedPTokens);

        IERC20(_asset).safeTransfer(_recipient, _amount);
    }

    /**
     * @dev Remove all assets from platform and send them to Vault contract.
     */
    function withdrawAll() external override virtual onlyVaultOrGovernor nonReentrant {
        // Withdraw all from Gauge
        (, uint256 gaugePTokens, uint256 totalPTokens) = _getTotalPTokens();
        _lpWithdraw(gaugePTokens);
        // Withdraws are proportional to assets held by 3Pool
        uint256[3] memory minWithdrawAmounts = [
            uint256(0),
            uint256(0),
            uint256(0)
        ];
        // Remove liquidity
        ICurvePool threePool = ICurvePool(platformAddress);
        threePool.remove_liquidity(totalPTokens, minWithdrawAmounts);
        // Transfer assets out of Vault
        // Note that Curve will provide all 3 of the assets in 3pool even if
        // we have not set PToken addresses for all of them in this strategy
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            IERC20 asset = IERC20(threePool.coins(i));
            asset.safeTransfer(vaultAddress, asset.balanceOf(address(this)));
        }
    }

    /**
     * @dev Get the total asset value held in the platform
     * @return balance    Total value of the asset in the platform
     */
    function checkBalance()
        public
        view
        virtual
        override
        returns (uint256 balance)
    {
        
    }

    /**
     * @dev Retuns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset
     */
    function supportsAsset(address _asset)
        external
        view
        override
        returns (bool)
    {
        return assetToPToken[_asset] != address(0);
    }

    /**
     * @dev Approve the spending of all assets by their corresponding pool tokens,
     *      if for some reason is it necessary.
     */
    function safeApproveAllTokens()
        external
        override
        onlyGovernor
        nonReentrant
    {
        _approveBase();
        // This strategy is a special case since it only supports one asset
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            _approveAsset(assetsMapped[i]);
        }
    }

    /**
     * @dev Calculate the total platform token balance (i.e. 3CRV) that exist in
     * this contract or is staked in the Gauge (or in other words, the total
     * amount platform tokens we own).
     * @return contractPTokens Amount of platform tokens in this contract
     * @return gaugePTokens Amount of platform tokens staked in gauge
     * @return totalPTokens Total amount of platform tokens in native decimals
     */
    function _getTotalPTokens()
        internal
        view
        virtual
        returns (
            uint256 contractPTokens,
            uint256 gaugePTokens,
            uint256 totalPTokens
        );

    /**
     * @dev Call the necessary approvals for the Curve pool and gauge
     * @param _asset Address of the asset
     */
    function _abstractSetPToken(address _asset, address _pToken)
        internal
        override
    {
        _approveAsset(_asset);
    }

    function _approveAsset(address _asset) internal {
        IERC20 asset = IERC20(_asset);
        // 3Pool for asset (required for adding liquidity)
        asset.safeApprove(platformAddress, 0);
        asset.safeApprove(platformAddress, type(uint256).max);
    }

    function _approveBase() internal virtual;

    /**
     * @dev Get the index of the coin
     */
    function _getCoinIndex(address _asset) internal view returns (uint256) {
        for (uint256 i = 0; i < 3; i++) {
            if (assetsMapped[i] == _asset) return i;
        }
        revert("Invalid 3pool asset");
    }
}
","keccak256":"0x0c0154d005431683277bae15e9ae7d88a211ad1a802c4fe5aa3d9276c15e8cf4","license":"agpl-3.0"},"contracts/strategies/DystopiaExchange.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IDystopiaRouter.sol";
import "hardhat/console.sol";

abstract contract DystopiaExchange {

    uint256 public constant BASIS_POINTS_FOR_SLIPPAGE = 4;

    IDystopiaRouter private dystRouter;

    function _setDystopiaRouter(address _dystRouter) internal {
        dystRouter = IDystopiaRouter(_dystRouter);
    }

    function _swapExactTokensForTokens(
        address inputToken,
        address middleToken,
        address outputToken,
        bool isStablePair0,
        bool isStablePair1,
        uint256 amountInput,
        address recipient
    ) internal returns (uint256) {

        IERC20(inputToken).approve(address(dystRouter), amountInput);

        uint256 amountOutMin = _getAmountsOut(address(inputToken), address(middleToken), address(outputToken), isStablePair0, isStablePair1, amountInput);
        // console.log("amountOutMin: ", amountOutMin);
        if (amountOutMin == 0) {
            return 0;
        }

        IDystopiaRouter.Route[] memory route = new IDystopiaRouter.Route[](2);
        route[0].from = inputToken;
        route[0].to = middleToken;
        route[0].stable = isStablePair0;
        route[1].from = middleToken;
        route[1].to = outputToken;
        route[1].stable = isStablePair1;

        uint[] memory amounts = dystRouter.swapExactTokensForTokens(
            amountInput,
            0,
            route,
            recipient,
            block.timestamp + 600
        );
    
        return amounts[2];
    }

    function _getAmountsOut(
        address inputToken,
        address middleToken,
        address outputToken,
        bool isStablePair0,
        bool isStablePair1,
        uint256 amountInput
    ) internal view returns (uint256) {

        IDystopiaRouter.Route[] memory route = new IDystopiaRouter.Route[](2);
        route[0].from = inputToken;
        route[0].to = middleToken;
        route[0].stable = isStablePair0;
        route[1].from = middleToken;
        route[1].to = outputToken;
        route[1].stable = isStablePair1;

        uint[] memory amounts = dystRouter.getAmountsOut(amountInput, route);

        return amounts[2];
    }

    function _addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to
    ) internal returns (uint amountA, uint amountB, uint liquidity) {

        IERC20(tokenA).approve(address(dystRouter), amountADesired);
        IERC20(tokenB).approve(address(dystRouter), amountBDesired);

        return dystRouter.addLiquidity(
            tokenA,
            tokenB,
            true,
            amountADesired,
            amountBDesired,
            amountAMin,
            amountBMin,
            to,
            block.timestamp + 600
        );

    }

    function _removeLiquidity(
        address tokenA,
        address tokenB,
        address lpToken,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to
    ) internal returns (uint amountA, uint amountB) {

        IERC20(lpToken).approve(address(dystRouter), liquidity);

        return dystRouter.removeLiquidity(
            tokenA,
            tokenB,
            true,
            liquidity,
            amountAMin,
            amountBMin,
            to,
            block.timestamp + 600
        );
    }

    uint256[49] private __gap;
}","keccak256":"0x4f63e92884164d401fe369d7af6189c5b92309c54d097b27fa1ad26a86ad3c3a","license":"MIT"},"contracts/strategies/DystopiaStrategy.sol":{"content":"// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.0;

/**
 * @title Dystopia Strategy
 * @notice Investment strategy for investing stablecoins via Dystopia Strategy
 * @author Stabl Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol"  ;

import { IRewardStaking } from "./IRewardStaking.sol";
import { DystopiaExchange } from "./DystopiaExchange.sol";
import { IConvexDeposits } from "./IConvexDeposits.sol";
import { IERC20, BaseCurveStrategy } from "./BaseCurveStrategy.sol";
import { StableMath } from "../utils/StableMath.sol";
import { Helpers } from "../utils/Helpers.sol";
import { AaveBorrowLibrary } from "../utils/AaveBorrowLibrary.sol";
import "../interfaces/IPriceFeed.sol";
import "../interfaces/IDystopiaLP.sol";
import "../interfaces/ISwapper.sol";
import "../connectors/IUserProxy.sol";
import "../connectors/IPenLens.sol";
import "../exchanges/BalancerExchange.sol";
import { IERC20, InitializableAbstractStrategy } from "../utils/InitializableAbstractStrategy.sol";
import { OvnMath } from "../utils/OvnMath.sol";
import "hardhat/console.sol";
contract DystopiaStrategy is InitializableAbstractStrategy, DystopiaExchange, BalancerExchange  {
    using StableMath for uint256;
    using SafeERC20 for IERC20;

    mapping(address => uint256 ) public assetToDenominator;

    IDystopiaLP  public  gauge;
    IDystopiaLP  public  dystPair;

    address public  dystRouter;

    bytes32 public poolId;


    IERC20 public primaryStable;
    IERC20 public middleToken;
    IERC20 public token0;
    IERC20 public token1;

    IERC20 public dystToken;



    IERC20 public penToken;
    IUserProxy public userProxy;
    IPenLens public penLens;


    /**
     * Initializer for setting up strategy internal state. This overrides the
     * InitializableAbstractStrategy initializer as Dystopia strategies don't fit
     * well within that abstraction.
     */
    function initialize(
        address _platformAddress, // dystToken address
        address _vaultAddress,
        address[] calldata _rewardTokenAddresses, // USDC
        address[] calldata _assets, // USDC & DAI etc
        address[] calldata _pTokens, // DystPair
        address _primaryStable, // USDC
        address _intermediatory_token // wMATIC
    ) external onlyGovernor initializer {

        token0 = IERC20(_assets[0]);
        token1 = IERC20(_assets[1]);
        primaryStable = IERC20(_primaryStable);
        middleToken = IERC20(_intermediatory_token);

        dystToken = IERC20(_platformAddress);

        uint256 assetCount = _assets.length;
        require(assetCount == _pTokens.length, "Invalid input arrays");
        for (uint256 i = 0; i < assetCount; i++) {
            assetToDenominator[_assets[i]] = 10 ** IERC20Metadata(_assets[i]).decimals();
        }
        super._initialize(
            _platformAddress,
            _vaultAddress,
            _rewardTokenAddresses,
            _assets,
            _pTokens
        );
    }
    function setParams(
        address _gauge,
        address _dystPair,
        address _dystRouter,
        address _balancerVault, 
        bytes32 _poolId,// _poolIdUsdcTusdDaiUsdt
        address _userProxy,
        address _penLens,
        address _penToken
    ) external onlyGovernor {
        setBalancerVault(_balancerVault);
        poolId = _poolId;
        gauge = IDystopiaLP(_gauge);
        dystPair = IDystopiaLP(_dystPair);
        _setDystopiaRouter(_dystRouter);
        userProxy = IUserProxy(_userProxy);
        penLens = IPenLens(_penLens);
        penToken = IERC20(_penToken);
    }

    function getReserves() internal view returns (uint256,uint256) {
        (uint256 reserve0, uint256 reserve1,) = dystPair.getReserves();
        require(reserve0 > (10 ** (IERC20Metadata(address(token0) ).decimals() - 3))  && reserve1 > (10 ** (IERC20Metadata(address(token1) ).decimals() - 3)), "Reserves too low");
        return (reserve0, reserve1) ;
    }

    function _deposit(
        address _asset,
        uint256 _amount
    )  internal {

        require(_asset == address(primaryStable), "Token not supported.");
        (uint256 reserve0, uint256 reserve1) = getReserves();
        _swapPrimaryStableToToken0();

        // count amount token1 to swap
        uint256 token1Balance = token1.balanceOf(address(this));
        uint256 amount0From1;
        if (token1Balance > 0) {
            amount0From1 = onSwap(
                poolId,
                IVault.SwapKind.GIVEN_IN,
                token1,
                token0,
                token1Balance
            );
        }

        uint256 token0Balance = token0.balanceOf(address(this));
        // console.log("token0", address(token0));
        // console.log("token1", address(token1));
        // console.log("token0Balance", token0Balance);
        // console.log("token1Balance", token1Balance);
        // console.log("reserve0", reserve0);
        // console.log("reserve1", reserve1);
        uint256 amountToken0ToSwap = _getAmountToSwap(
            token0Balance,
            reserve0,
            reserve1,
            assetToDenominator[address(token0)],
            assetToDenominator[address(token1)],
            1,
            poolId,
            token0,
            token1
        );
        // console.log("amountToken0ToSwap", amountToken0ToSwap);
        // swap some of token0 to token1
        swap(
            poolId,
            IVault.SwapKind.GIVEN_IN,
            IAsset(address(token0)),
            IAsset(address(token1)),
            address(this),
            address(this),
            amountToken0ToSwap,
            0
        );

        // add liquidity
        token0Balance = token0.balanceOf(address(this));
        token1Balance = token1.balanceOf(address(this));

        _addLiquidity(
            address(token0),
            address(token1),
            token0Balance,
            token1Balance,
            OvnMath.subBasisPoints(token0Balance, BASIS_POINTS_FOR_SLIPPAGE),
            OvnMath.subBasisPoints(token1Balance, BASIS_POINTS_FOR_SLIPPAGE),
            address(this)
        );

        uint256 lpTokenBalance = dystPair.balanceOf(address(this));
         // console.log("Dyst LP Token Balance: ", lpTokenBalance);
        _stakeToPenrose(lpTokenBalance);

    }

    function deposit(
        address _asset,
        uint256 _amount
    )   external
        override
        onlyVault
        nonReentrant {

        require(_asset == address(primaryStable), "Token not supported.");
        _deposit(_asset, _amount);
    }

    function depositAll() external override onlyVault nonReentrant {
        _deposit(address(primaryStable), primaryStable.balanceOf(address(this)));
    }
    function withdraw(
        address _beneficiary,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant  {

        require(_asset == address(primaryStable), "Token not supported.");
        (uint256 reserve0, uint256 reserve1) = getReserves();

        // Fetch amount of penPool LP currently staked
        address userProxyThis = penLens.userProxyByAccount(address(this));
        address stakingAddress = penLens.stakingRewardsByDystPool(address(dystPair));
        uint256 lpTokenBalance = IERC20(stakingAddress).balanceOf(userProxyThis);

        if (lpTokenBalance > 0) {
            // count amount to unstake
            uint256 totalLpBalance = dystPair.totalSupply();
            uint256 lpTokensToWithdraw = _getAmountLpTokensToWithdraw(
                OvnMath.addBasisPoints(_amount, BASIS_POINTS_FOR_SLIPPAGE),
                reserve0,
                reserve1,
                totalLpBalance,
                assetToDenominator[address(token0)],
                assetToDenominator[address(token1)],
                poolId,
                token0,
                token1
            );

            if (lpTokensToWithdraw > lpTokenBalance) {
                lpTokensToWithdraw = lpTokenBalance;
            }

            userProxy.unstakeLpAndWithdraw(address(dystPair), lpTokensToWithdraw);
            uint256 unstakedLPTokenBalance = dystPair.balanceOf(address(this));
            // remove liquidity
            _removeLiquidity(
                address(token0),
                address(token1),
                address(dystPair),
                unstakedLPTokenBalance,
                OvnMath.subBasisPoints(reserve0 * unstakedLPTokenBalance / totalLpBalance, BASIS_POINTS_FOR_SLIPPAGE),
                OvnMath.subBasisPoints(reserve1 * unstakedLPTokenBalance / totalLpBalance, BASIS_POINTS_FOR_SLIPPAGE),
                address(this)
            );
        }
        _swapAssetsToPrimaryStable();
        uint256 primaryStableBalance = primaryStable.balanceOf(address(this));
         // console.log("Withdraw USDC: ", primaryStableBalance);
        primaryStable.safeTransfer(_beneficiary, primaryStableBalance);
       
    }

    function withdrawAll() external override onlyVaultOrGovernor nonReentrant  {
        (uint256 reserve0, uint256 reserve1) = getReserves();
        _withdrawFromDystopiaAndStakeToPenrose();
        // Fetch amount of penPool LP currently staked
        address userProxyThis = penLens.userProxyByAccount(address(this));
        address stakingAddress = penLens.stakingRewardsByDystPool(address(dystPair));
        uint256 lpTokenBalance = IERC20(stakingAddress).balanceOf(userProxyThis);
        if (lpTokenBalance == 0) {
            return;
        }
        userProxy.unstakeLpAndWithdraw(address(dystPair), lpTokenBalance);
        uint256 unstakedLPTokenBalance = dystPair.balanceOf(address(this));
        if (unstakedLPTokenBalance > 0) {
            uint256 totalLpBalance = dystPair.totalSupply();
            uint256 amountOutUsdcMin = reserve0 * unstakedLPTokenBalance / totalLpBalance;
            uint256 amountOutOtherMin = reserve1 * unstakedLPTokenBalance / totalLpBalance;
            // remove liquidity
            _removeLiquidity(
                address(token0),
                address(token1),
                address(dystPair),
                unstakedLPTokenBalance,
                OvnMath.subBasisPoints(amountOutUsdcMin, BASIS_POINTS_FOR_SLIPPAGE),
                OvnMath.subBasisPoints(amountOutOtherMin, BASIS_POINTS_FOR_SLIPPAGE),
                address(this)
            );
        }

        _swapAssetsToPrimaryStable();
        uint256 primaryStableBalance = primaryStable.balanceOf(address(this));
         // console.log("Withdraw Primary Stable: ", primaryStableBalance);
        primaryStable.safeTransfer(vaultAddress, primaryStableBalance);
        _collectRewards();
    }


    function checkBalance()
        external
        view
        override
        returns (uint256 balance)
    {
        uint256 primaryStableBalance = primaryStable.balanceOf(address(this));
        uint256 token0Balance = token0.balanceOf(address(this));
        uint256 token1Balance = token1.balanceOf(address(this));

        // Fetch amount of penPool LP currently staked
        address userProxyThis = penLens.userProxyByAccount(address(this));
         // console.log("dystPair", address(dystPair));
        address stakingAddress = penLens.stakingRewardsByDystPool(address(dystPair));
         // console.log("stakingAddress: ", stakingAddress);
        uint256 lpTokenBalance = IERC20(stakingAddress).balanceOf(userProxyThis);
        lpTokenBalance += gauge.balanceOf(address(this));
        if (lpTokenBalance > 0) {
            uint256 totalLpBalance = dystPair.totalSupply();
            (uint256 reserve0, uint256 reserve1) = getReserves();
            token0Balance += reserve0 * lpTokenBalance / totalLpBalance;
            token1Balance += reserve1 * lpTokenBalance / totalLpBalance;
        }
        // console.log("tokenBalance", token0Balance, token1Balance);

        uint256 primaryStableBalanceFromToken0;
        if ( (address(token0) != address(primaryStable))  ) {
            if (token0Balance > 0) {
                primaryStableBalanceFromToken0 = onSwap(
                    poolId,
                    IVault.SwapKind.GIVEN_IN,
                    token0,
                    primaryStable,
                    token0Balance
                );
                // console.log("Token0 swap -  primaryStableBalanceFromToken0 ", primaryStableBalanceFromToken0);
            }
        } else {
            primaryStableBalanceFromToken0 += token0Balance;
        }

        uint256 primaryStableBalanceFromToken1;
        if ( (address(token1) != address(primaryStable))  ) {
            if (token1Balance > 0) {
                primaryStableBalanceFromToken1 = onSwap(
                    poolId,
                    IVault.SwapKind.GIVEN_IN,
                    token1,
                    primaryStable,
                    token1Balance
                );
                // console.log("Token1 swap -  primaryStableBalanceFromToken1 ", primaryStableBalanceFromToken1);
            }
        } else {
            primaryStableBalanceFromToken1 += token1Balance;
        }
         // console.log("primaryStableBalanceFromToken0: ", primaryStableBalanceFromToken0);
         // console.log("primaryStableBalanceFromToken1: ", primaryStableBalanceFromToken1);
        return primaryStableBalanceFromToken0 + primaryStableBalanceFromToken1 + primaryStableBalance;
    }

    function collectRewardTokens()
        external
        override
        onlyHarvester
        nonReentrant
    {
        _collectRewards();
    }
    function _collectRewards() internal {
         console.log("Starting collection of rewards");
        _withdrawFromDystopiaAndStakeToPenrose();

         console.log("_withdrawFromDystopiaAndStakeToPenrose called");
        // claim rewards
        userProxy.claimStakingRewards();
         console.log("claimStakingRewards called");
        // sell rewards
        uint256 totalUsdc;

        uint256 dystBalance = dystToken.balanceOf(address(this));
         console.log("dystBalance: ", dystBalance);
        if (dystBalance > 0) {
            uint256 dystUsdc = _swapExactTokensForTokens(
                address(dystToken),
                address(middleToken),
                address(token0),
                false,
                false,
                dystBalance,
                address(this)
            );
            totalUsdc += dystUsdc;
        }
         console.log("totalUsdc=",totalUsdc);
        uint256 penBalance = penToken.balanceOf(address(this));
        console.log("penBalance: ", penBalance);
        if (penBalance > 0) {
            uint256 penUsdc = _swapExactTokensForTokens(
                address(penToken),
                address(middleToken),
                address(primaryStable),
                false,
                false,
                penBalance,
                address(this)
            );
            console.log("penUsdc",penUsdc);
            totalUsdc += penUsdc;
        }
        uint256 balance = primaryStable.balanceOf(address(this));
        console.log("balance: ", balance);
        emit RewardTokenCollected(
            harvesterAddress,
            address(primaryStable),
            balance
        );
        primaryStable.transfer(harvesterAddress, balance);
    }
    function _withdrawFromDystopiaAndStakeToPenrose() internal {
        uint256 lpTokenBalance = gauge.balanceOf(address(this));
        if (lpTokenBalance > 0) {
            // claim rewards
            address[] memory token = new address[](1);
            token[0] = address(dystToken);
            gauge.getReward(address(this), token);

            // withdraw LP tokens and stake
            gauge.withdrawAll();
            uint256 lpTokenBalance = dystPair.balanceOf(address(this));
            dystPair.approve(address(userProxy), lpTokenBalance);
            userProxy.depositLpAndStake(address(dystPair), lpTokenBalance);
        }
    }
    function _stakeToPenrose(uint256 _lpTokenAmount) internal {
        dystPair.approve(address(userProxy), _lpTokenAmount);
        userProxy.depositLpAndStake(address(dystPair), _lpTokenAmount);
        uint256 penBalance = penToken.balanceOf(address(this));
         // console.log("penBalance: ", penBalance);
    }

    function _swapAssetsToPrimaryStable() internal {
        if ( (address(token0) != address(primaryStable)) && (token0.balanceOf(address(this)) > 0) )  {
             // console.log("Swapping token0");
            swap(
                poolId,
                IVault.SwapKind.GIVEN_IN,
                IAsset(address(token0)),
                IAsset(address(primaryStable)),
                address(this),
                address(this),
                token0.balanceOf(address(this)),
                0
            );
        }
        if ( (address(token1) != address(primaryStable)) && (token1.balanceOf(address(this)) > 0) )  {
             // console.log("Swapping token1");
            swap(
                poolId,
                IVault.SwapKind.GIVEN_IN,
                IAsset(address(token1)),
                IAsset(address(primaryStable)),
                address(this),
                address(this),
                token1.balanceOf(address(this)),
                0
            );
        }
    }
    function _swapPrimaryStableToToken0() internal {
        uint256 primaryStableBalance = primaryStable.balanceOf(address(this));
        if (address(primaryStable) != address(token0)) {
            swap(
                poolId,
                IVault.SwapKind.GIVEN_IN,
                IAsset(address(primaryStable)),
                IAsset(address(token0)),
                address(this),
                address(this),
                primaryStableBalance,
                0
            );
        }
    }


     /**
     * @dev Retuns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset
     */
    function supportsAsset(address _asset)
        external
        view
        override
        returns (bool)
    {
        return _asset == address(primaryStable);
    }

    /**
     * @dev Approve the spending of all assets by their corresponding cToken,
     *      if for some reason is it necessary.
     */
    function safeApproveAllTokens() external override {
        // NOT NEEDED
    }

    /**
     * @dev Internal method to respond to the addition of new asset / cTokens
     *      We need to approve the cToken and give it permission to spend the asset
     * @param _asset Address of the asset to approve
     * @param _cToken The cToken for the approval
     */
    function _abstractSetPToken(address _asset, address _cToken)
        internal
        override
    {
        // NOT NEEDED
    }
   
}
